    ul>li:first-child>.reference.internal,
    a,
    span.pre {
        color: #44b78b;
    }

    .admonition,
    .admonition.warning,
    .admonition.note,
    .admonition.danger,
    pre.literal-block,
    .literal-block {
        background-color: #181818;
